    def euler_phi(n):

        """正整数 n に対して、n と互いに素な n 以下の正整数の個数"""

        ret = n

        for i in range(2, int(n**0.5) + 1):

            if n % i == 0:

                ret = ret // i * (i - 1)

                while n % i == 0:

                    n //= i

        if n > 1:

            ret -= ret // n

        return ret



    def divisor_list(n):

        """n の正の約数のリスト"""

        ret = set()

        for k in range(1, int(n**0.5) + 1):

            if n % k == 0:

                ret.update({k, n // k})

        return sorted(ret)



